import sys
from io import StringIO
import unittest
import logging
logging.basicConfig(level=logging.DEBUG)

def resolve():
    n = int(input())
    m = []
    import collections
    for i in range(n):
        m.append(input())
    line = []
    for i in range(n + 1):
        line.append(collections.deque([]))

    for i in range(n):
        for j in range(i,n):
            if m[j][i] == "1":
                line[i+1].append(j+1)

    # 各点が所属する位置
    group = [300] * (n+1)
    group_min = 300
    group_max = 300
    group[1] = 300

    can = True
    for i in range(1, n + 1):
        #print("見るのは{0}です".format(i))
        lineto = []
        for j in range(i, 0, -1):
            #print("seek:{0}".format(j))
            usecount = 0
            for k in range( len(line[j]) ):
                #print("k = {0}, data = {1}".format(k, line[j]))
                if i < line[j][k]:
                    #print(" no see [{0}] = {1}".format(k, line[j][k]))
                    break
                usecount += 1
                item = line[j][k]
                #print("★pop = {0}".format(item))
                lineto.append(j)
            for k in range(usecount):
                item = line[j].popleft()
        #print("行くべきは{0}".format(lineto))
        groupto = list(map(lambda x: group[x], lineto))
        if len(groupto) == 0:
            #print("つながる先はないのでスキップ")
            continue
        #print("つながるべきグループは{0}".format(groupto))
        if (max(groupto) - min(groupto)) > 2:
            #print("繋がれません")
            can = False
            break
        elif (max(groupto) - min(groupto)) == 1:
            #print("繋がれません")
            can = False
            break
        elif (max(groupto) - min(groupto)) == 2:
            target = (max(groupto) + min(groupto)) // 2
            #print("間に存在しないといけません。 {1} = {0}".format(target, i))
            group[i] = target
        else:
            # なるべく差が開いている方がに送る
            if (max(groupto) + 1) > group_max:
                target = max(groupto) + 1
                #print("maxを更新できるのでそちらに追加(max, min) =  {0} = {1}なので{2} = {3}".format(max(groupto), min(groupto), i, target))
                group[i] = target
                group_max += 1
            elif (min(groupto) + 1) > group_min:
                target = min(groupto) - 1
                #print("minを後進できるのでそちらに追加(max, min) =  {0} = {1}なので{2} = {3}".format(max(groupto), min(groupto), i, target))
                group[i] = target
                group_min -= 1
            elif abs(max(groupto)) >= abs(min(groupto)):
                target = max(groupto) + 1
                #print("正のほうがおおきいか一緒なのでそちらに追加(max, min) =  {0} = {1}なので{2} = {3}".format(max(groupto), min(groupto), i, target))
                group[i] = target
                group_max += 1
            else:
                target = min(groupto) - 1
                #print("負のほうが遠いのでそちらに追加(max, min) =  {0} = {1}なので{2} = {3}".format(max(groupto), min(groupto), i, target))
                group[i] = target
                group_min -= 1

    group = group[1:]
    print(group)
    if None in group:
        print(-1)
    elif can:
        print(1 + max(group) - min(group))
    else:
        print(-1)

class TestClass(unittest.TestCase):
    def assertIO(self, input, output):
        stdout, stdin = sys.stdout, sys.stdin
        sys.stdout, sys.stdin = StringIO(), StringIO(input)
        resolve()
        sys.stdout.seek(0)
        out = sys.stdout.read()[:-1]
        sys.stdout, sys.stdin = stdout, stdin
        self.assertEqual(out, output)
